import React, { useMemo,useState } from 'react'
import axios from 'axios';
import './Carouseler.css'
const Carouseler = () => {

   const [images, setImages] = useState([]);

    const fetchAPI = async()=>{
    const response = await axios.get('https://api.unsplash.com/photos/?client_id=YbnWcUQHyapMRVl5x8yCOn_I9inhl0dTOpCfNjy99no');
    const data = response.data;
    setImages(data);
    }
    useMemo(
      fetchAPI,[]
    )
    const [index, setIndex] = useState(0);

    const length = images.length;
    const handlePrevious = () => {
      const newIndex = index - 1;
      setIndex(newIndex < 0 ? length - 1 : newIndex);
  };

  const handleNext = () => {
    const newIndex = index + 1;
    setIndex(newIndex >= length ? 0 : newIndex);
  };


  return (

    <div className='container'> 
        <div className="carousel">
            <button onClick={handlePrevious}>Previous</button>
            < img  src={images[index].urls.small} alt='photo'></img>
            <button onClick={handleNext}>Next</button>
       </div>
    </div>
  );
};
export default Carouseler
